import numpy as np
import random


def random_string(n):
    chrs = 'abcdefghijklmnopqrstuvwxyz'
    inds = np.random.randint(0,len(chrs), size=n)
    return ''.join([chrs[i] for i in inds])

def random_pronounceable_string(n_parts=1):
    """
    From urbit's hoon.hoon
    """
    prefixes = ""
    suffixes = ""
    uint8_pairs = [(random.randint(0,255),random.randint(0,255)) for _ in xrange(n_parts)]
    return "-".join([prefixes[3*i:3*i+3]+suffixes[3*j:3*j+3] for (i,j) in uint8_pairs])

def random_indices(n,k):
    """choose a random subset of k elements of {0,1,2,...,n-1}"""
    return np.sort(np.random.permutation(n)[:k])

def random_indices_with_replacement(n,k):
    return np.floor(np.random.rand(k)*n).astype('int')
